import Rock from '../Choices/Rock.js';
import Paper from '../Choices/Paper.js';
import Scissors from '../Choices/Scissors.js';
import Circle from '../Choices/Circle.js';
import React from 'react';


export const robot = (state=<Circle/>,action) => {
    switch(action.type){
        case 'ROBOT_ROCK':
        return <Rock id='animation1'/>

        case 'ROBOT_PAPER':
        return <Paper id='animation2'/>

        case 'ROBOT_SCISSORS':
        return <Scissors id='animation3'/>

        default:
        return state
    }
}
