import Rock from '../Choices/Rock.js';
import Paper from '../Choices/Paper.js';
import Scissors from '../Choices/Scissors.js';
import React from 'react';



export const choice = (state='EMPTY',action) => {
    switch(action.type){
        case 'ROCK':
        return <Rock/>

        case 'PAPER':
        return <Paper/>

        case 'SCISSORS':
        return <Scissors/>

        default:
        return state
    }
}
