import React,{useEffect} from 'react';
import {useSelector,useDispatch} from 'react-redux';
import {housePicked} from './actions/index.js';
import Rock from './Choices/Rock.js';
import Paper from './Choices/Paper.js';
import Scissors from './Choices/Scissors.js';
import Winner from './Winner';
import Circle from './Choices/Scissors.js';


const Match = () => {
    const dispatch = useDispatch();
    const you = useSelector(state => state.choice);
    const house = useSelector(state => state.robot);

    function random (){
        let number = Math.random();
       if(number>0 && number <=  0.33){
           return 'ROBOT_ROCK'
       }else if(number > 0.33 && number <= 0.66){
           return 'ROBOT_PAPER'
       }else{
           return 'ROBOT_SCISSORS'
       }
    }

   var result = '';

    function waiting (){
        if(house !== <Circle/>){
            return decision()
        }
    }

    const decision = () => {
        if(you===<Paper/> && house === <Rock/>){
            return result = <Winner decision='YOU WIN' />
        }else if(you===<Paper/> && house === <Scissors/>){
            return result=<Winner decision='YOU LOSE'/>
        }else if(you===house){
            return result = <Winner decision='YOU TIED'/>
        }else if(you===<Rock/> && house === <Paper/>){
            return result = <Winner decision='YOU LOSE'/>
        }else if(you === <Rock/> && house === <Scissors/>){
            return result = <Winner decision='YOU WIN'/>
        }else if(you === <Scissors/> && house === <Rock/>){
            return result = <Winner decision='YOU LOSE'/>
        }else{
            return result = <Winner decision='YOU WIN'/>
        }
    }


    useEffect(()=> {
        const timer = setTimeout(
            () => dispatch(housePicked(random())),
            2000    
          );
          return () => clearTimeout(timer);
    },[dispatch]);


    return(
        <div className='match'>
            <div className='youGroup'> 
                <h4>YOU PICKED</h4>
                <div className='youPicked'>
                    {you}
                </div>
            </div>
            <div className='winner'>
                {waiting}
            </div>
            <div className='houseGroup'> 
                <h4>THE HOUSE PICKED</h4>
                <div className='housePicked'>
                    {house}
                </div>
            </div>
        </div>
    )
}

export default Match;
