import React from 'react';
import Score from './Score';
import Game from './Game';
import Rules from './Rules';
import {useSelector} from 'react-redux'

const App = () => {

    const state = useSelector(state => state.rules)
    function opacity (){
        if(state===true){
            return 'none'
        }else{
            return 'block'
        }
    }

    return(
        <div className='body'>
            <div style={{display:opacity()}}>
                <Score/>
                <Game/>
                <Rules/>
            </div>
        </div>
    )
}

export default App;
