import React from 'react';
import triangle from './images/bg-triangle.svg';
import Rock from './Choices/Rock';
import Paper from './Choices/Paper';
import Scissors from './Choices/Scissors';
import {useDispatch} from 'react-redux';
import {youPicked} from './actions/index.js';

const Game = () => {
    const dispatch = useDispatch();
    return (
        <div className='game'>
            <div  className='triangle' >
                <img src={triangle} alt=''></img> 
                <div onClick={()=>dispatch(youPicked('ROCK'))}className='gameRock'>
                    <Rock/>
                </div>
                <div onClick={()=>dispatch(youPicked('PAPER'))} className='gamePaper'>
                    <Paper/>
                </div>
                <div onClick={()=>dispatch(youPicked('SCISSORS'))} className='gameScissors'>
                    <Scissors/>
                </div>
            </div>

        </div>
    )
}

export default Game;
