import { bindActionCreators } from "redux"

export const rulesClick = () => {
    return {
        type:'RULES',
    }
}

export const youPicked = (pick) => {
    return{
        type:pick
    }
}

export const housePicked = (pick) => {
    return{
        type:pick
    }
}

export const finalAnswer = (pick,robo) => {
    return{
        type:pick,
        payload:robo
    }
}
