import rules from './images/image-rules.svg';
import x from './images/icon-close.svg';
import { useSelector,useDispatch } from 'react-redux';
import {rulesClick} from './actions/index.js';


const Rules = () => {
const dispatch = useDispatch();
const show = useSelector(state => state.rules)
    return (
        <div>
            <div className='rulesImage'>
                <div className='topRules'>
                    <h1>RULES</h1>
                    <img src={x} alt=''></img>
                </div>
                <img src={rules} alt=''></img>
            </div>
            <div className='rules' onClick={()=>dispatch(rulesClick())}>
                <div>R U L E S</div>
            </div>
        </div>
    )
}

export default Rules;
