import React,{useEffect} from 'react';
import {useSelector,useDispatch} from 'react-redux';
import {housePicked} from './actions/index.js';

const Match = () => {
    const dispatch = useDispatch();

    function random (){
        let number = Math.random();
       if(number>0 && number <=  0.33){
           return 'ROBOT_ROCK'
       }else if(number > 0.33 && number <= 0.66){
           return 'ROBOT_PAPER'
       }else{
           return 'ROBOT_SCISSORS'
       }
    }

 useEffect(()=> { const timer = setTimeout(useDispatch(housePicked(random())),3000);})   


const youPicked = useSelector(state => state.choice);
const housePicked = useSelector(state => state.robot);

    return(
        <div className='match'>
            <div className='youGroup'> 
                <h4>YOU PICKED</h4>
                <div className='youPicked'>
                    {youPicked}
                </div>
            </div>
            <div className='winner'>


            </div>
            <div className='houseGroup'> 
                <h4>THE HOUSE PICKED</h4>
                <div className='housePicked'>
                    {housePicked}
                </div>
            </div>
        </div>
    )
}

export default Match;
