import React from 'react';
import Rock from '../Choices/Rock.js';
import Paper from '../Choices/Paper.js';
import Scissors from '../Choices/Scissors.js';
import Winner from '../Winner';
import {useSelector} from 'react-redux';


const house = useSelector(state => state.robot);

export const final = (state='',action) =>{
    if(action.type===<Paper/> && house === <Rock/>){
        return <Winner decision='YOU WIN' />
    }else if(action.type===<Paper/> && house === <Scissors/>){
        return <Winner decision='YOU LOSE'/>
    }else if(action.type===house){
        return <Winner decision='YOU TIED'/>
    }else if(action.type===<Rock/> && house === <Paper/>){
        return <Winner decision='YOU LOSE'/>
    }else if(action.type === <Rock/> && house === <Scissors/>){
        return <Winner decision='YOU WIN'/>
    }else if(action.type === <Scissors/> && house === <Rock/>){
        return <Winner decision='YOU LOSE'/>
    }else if(action.type === <Scissors/> && house === <Paper/>){
        return <Winner decision='YOU WIN'/>
    }else{
        return state
    }
}
